mapper.select:
select *
from coupon_ruler
where id=#{value}

select cc.account,c.consumptionDate,c.couponValue,c.status
from coupon c,consumer cc
where c.rulerId=#{conCurrentCouponRulerId} and (c.status="1" or c.status="2") and c.ownerId=cc.id

  and c.consumptionDate >= #{startDate}


  and  c.consumptionDate <= #{endDate}

mapper.update:
update coupon_application


   consumptionValue=#{consumptionValue},


   couponValue=#{couponValue},


   status=#{status}


where id=#{id}

update coupon
set status="1"
where id=#{id}

update coupon
set status="3"
where id=#{id}

mapper.select:
select contractAddress
from coupon
where id=#{id}

mapper.update:
update coupon_pay_application
set status="1"
where applicationCode=#{value}

mapper.select:
select * from couponRuler where merchantId=#{id}

    and issueDate >= #{startDate}


    and issueDate <= #{endDate}

select * from couponRuler where merchantId=#{id}

    and withdarwDate >= #{startDate}


    and withdrawDate <= #{endDate}

mapper.update:
update coupon
set ownerId = #{targetId}
where id = #{couponsId}
